pragma solidity ^0.8.27;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

/**
 * @title perennialprediction
 * @dev Advanced prediction market for public goods and environmental projects
 */
contract perennialprediction is Ownable, ReentrancyGuard, Pausable {
    using ECDSA for bytes32;

    // Optimize storage by packing related variables
    struct MarketCore {
        string title;
        string description;
        uint64 endTime;
        bool isResolved;
        bool isHyperLocal;
        MarketType marketType;
    }

    struct MarketData {
        uint128 yesShares;
        uint128 noShares;
        uint128 totalStake;
        uint128 minStake;
        uint128 maxStake;
        uint64 reputationRequired;
        bool outcome;
    }

    struct MarketLocation {
        // Change to int256 to handle more precise coordinates
        int256 latitude;  // Multiplied by 1e18 for precision
        int256 longitude; // Multiplied by 1e18 for precision
        uint128 poolId;
        address creator;
    }

    struct UserPosition {
        uint128 yesShares;
        uint128 noShares;
        uint128 stakedAmount;
        uint64 lastInteractionTime;
        bool hasClaimedRewards;
    }

    struct UserReputation {
        uint128 score;
        uint64 marketsCreated;
        uint64 successfulPredictions;
        uint128 totalStaked;
        uint64 lastUpdateTime;
    }

    // Enums
    enum MarketType { FINITE, INFINITE, HYBRID }

    // Constants - packed into single storage slots
    uint64 public constant REPUTATION_THRESHOLD = 100;
    uint64 public constant MIN_STAKE_PERIOD = 1 days;
    uint64 public constant MAX_STAKE_PERCENTAGE = 20;
    uint64 public constant EARLY_UNSTAKE_PENALTY = 10;

    // State variables
    IERC20 public immutable stakingToken;
    uint64 public marketCount;

    // Mappings - optimized for gas efficiency
    mapping(uint256 => MarketCore) public marketCores;
    mapping(uint256 => MarketData) public marketData;
    mapping(uint256 => MarketLocation) public marketLocations;
    mapping(uint256 => mapping(address => UserPosition)) public positions;
    mapping(address => UserReputation) public userReputations;
    mapping(address => bool) public isMarketCreator;

    // Events
    event MarketCreated(
        uint64 indexed marketId,
        address indexed creator,
        string title,
        MarketType marketType,
        uint64 endTime,
        bool isHyperLocal,
        int256 latitude,
        int256 longitude
    );

    event SharesTraded(
        uint64 indexed marketId,
        address indexed user,
        bool isYes,
        uint128 amount,
        uint128 price,
        bool isBuy
    );

    event MarketResolved(uint64 indexed marketId, bool outcome);
    event ReputationUpdated(address indexed user, uint128 newScore);
    event RewardsClaimed(uint64 indexed marketId, address indexed user, uint128 amount);
    event MarketCreatorAdded(address indexed creator);

    // Modifiers
    modifier onlyAuthorizedCreator() {
        require(isMarketCreator[msg.sender] || owner() == msg.sender, "Not authorized");
        _;
    }

    modifier validMarket(uint64 _marketId) {
        require(_marketId > 0 && _marketId <= marketCount, "Invalid market ID");
        _;
    }

    modifier marketActive(uint64 _marketId) {
        require(!marketCores[_marketId].isResolved, "Market resolved");
        require(block.timestamp < marketCores[_marketId].endTime, "Market ended");
        _;
    }

    constructor(address _stakingToken) Ownable(msg.sender) {
        stakingToken = IERC20(_stakingToken);
    }

    // Core market functions
    function createMarket(
        MarketParams calldata params
    ) external onlyAuthorizedCreator whenNotPaused nonReentrant returns (uint64) {
        require(bytes(params.title).length > 0, "Invalid title");
        require(bytes(params.description).length > 0, "Invalid description");
        require(params.endTime > uint64(block.timestamp), "Invalid end time");
        require(params.minStake > 0, "Min stake must be > 0");

        // Convert to smallest unit for comparison
        uint256 minStakeInSmallestUnit = uint256(params.minStake) * 1e18;
        uint256 maxStakeInSmallestUnit = uint256(params.maxStake) * 1e18;
        require(maxStakeInSmallestUnit > minStakeInSmallestUnit, "Max stake must be > min stake");

        require(params.reputationRequired >= 0, "Invalid reputation requirement");

        // Update coordinate validation
        if (params.isHyperLocal) {
            // Check latitude range (-90 to 90) * 1e18
            require(
                params.latitude >= -90e18 && 
                params.latitude <= 90e18, 
                "Invalid latitude"
            );
            // Check longitude range (-180 to 180) * 1e18
            require(
                params.longitude >= -180e18 && 
                params.longitude <= 180e18, 
                "Invalid longitude"
            );
        }

        marketCount++;
        uint64 newMarketId = marketCount;

        _createMarketCore(newMarketId, params);
        _createMarketData(newMarketId, params);
        _createMarketLocation(newMarketId, params);
        _updateCreatorReputation(msg.sender);

        emit MarketCreated(
            newMarketId,
            msg.sender,
            params.title,
            params.marketType,
            params.endTime,
            params.isHyperLocal,
            params.latitude,
            params.longitude
        );

        return newMarketId;
    }

    // Internal functions to handle market creation
    function _createMarketCore(uint64 _marketId, MarketParams calldata _params) internal {
        marketCores[_marketId] = MarketCore({
            title: _params.title,
            description: _params.description,
            endTime: _params.endTime,
            isResolved: false,
            isHyperLocal: _params.isHyperLocal,
            marketType: _params.marketType
        });
    }

    function _createMarketData(uint64 _marketId, MarketParams calldata _params) internal {
        marketData[_marketId] = MarketData({
            yesShares: 0,
            noShares: 0,
            totalStake: 0,
            minStake: _params.minStake,
            maxStake: _params.maxStake,
            reputationRequired: _params.reputationRequired,
            outcome: false
        });
    }

    function _createMarketLocation(uint64 _marketId, MarketParams calldata _params) internal {
        marketLocations[_marketId] = MarketLocation({
            latitude: _params.latitude,
            longitude: _params.longitude,
            poolId: 0,
            creator: msg.sender
        });
    }

    // Helper struct to avoid stack too deep
    struct MarketParams {
        string title;
        string description;
        uint64 endTime;
        bool isHyperLocal;
        int256 latitude;   // Changed to int256
        int256 longitude;  // Changed to int256
        MarketType marketType;
        uint128 minStake;
        uint128 maxStake;
        uint64 reputationRequired;
    }

    // Trading functions
    function tradeShares(
        uint64 _marketId,
        bool _isYes,
        uint128 _amount,
        bool _isBuy
    ) external validMarket(_marketId) marketActive(_marketId) nonReentrant whenNotPaused {
        MarketData storage mData = marketData[_marketId];
        UserPosition storage position = positions[_marketId][msg.sender];

        // Convert amount to wei/smallest unit for comparison
        uint256 amountInSmallestUnit = uint256(_amount) * 1e18;
        uint256 minStakeInSmallestUnit = uint256(mData.minStake) * 1e18;
        uint256 maxStakeInSmallestUnit = uint256(mData.maxStake) * 1e18;

        require(amountInSmallestUnit >= minStakeInSmallestUnit, "Below min stake");
        require(amountInSmallestUnit <= maxStakeInSmallestUnit, "Above max stake");
        require(
            userReputations[msg.sender].score >= mData.reputationRequired,
            "Insufficient reputation"
        );

        uint128 price = _calculatePrice(_marketId, _isYes, _amount, _isBuy);

        if (_isBuy) {
            require(
                stakingToken.transferFrom(msg.sender, address(this), price),
                "Transfer failed"
            );

            if (_isYes) {
                mData.yesShares += _amount;
                position.yesShares += _amount;
            } else {
                mData.noShares += _amount;
                position.noShares += _amount;
            }
            position.stakedAmount += price;
            mData.totalStake += price;
        } else {
            uint128 shares = _isYes ? position.yesShares : position.noShares;
            require(shares >= _amount, "Insufficient shares");

            uint128 penalty = _calculatePenalty(_marketId, msg.sender, _amount);
            uint128 payout = price - penalty;

            if (_isYes) {
                mData.yesShares -= _amount;
                position.yesShares -= _amount;
            } else {
                mData.noShares -= _amount;
                position.noShares -= _amount;
            }

            position.stakedAmount -= payout;
            mData.totalStake -= payout;

            require(stakingToken.transfer(msg.sender, payout), "Transfer failed");
        }

        position.lastInteractionTime = uint64(block.timestamp);
        emit SharesTraded(_marketId, msg.sender, _isYes, _amount, price, _isBuy);
    }

    // Internal price calculation functions
    function _calculatePrice(
        uint64 _marketId,
        bool _isYes,
        uint128 _amount,
        bool _isBuy
    ) internal view returns (uint128) {
        MarketData storage mData = marketData[_marketId];
        uint256 totalShares = _isYes ? mData.yesShares : mData.noShares;
        uint256 otherShares = _isYes ? mData.noShares : mData.yesShares;

        // Convert amount to smallest unit for calculations
        uint256 amountInSmallestUnit = uint256(_amount) * 1e18;

        // Dynamic pricing using LMSR-inspired formula with decimal support
        uint256 basePrice = (totalShares * 1e18) / (totalShares + otherShares + 1);
        uint256 priceImpact = (amountInSmallestUnit * 1e18) / (mData.totalStake + 1);

        if (_isBuy) {
            return uint128((basePrice + ((priceImpact * (_isYes ? 1 : 2)) / 100)) / 1e18);
        } else {
            return uint128((basePrice - ((priceImpact * (_isYes ? 2 : 1)) / 100)) / 1e18);
        }
    }

    function _calculatePenalty(
        uint64 _marketId,
        address /* _user */, // Comment out unused parameter
        uint128 _amount
    ) internal view returns (uint128) {
        UserPosition storage position = positions[_marketId][msg.sender];
        uint64 timeHeld = uint64(block.timestamp) - position.lastInteractionTime;

        if (timeHeld >= MIN_STAKE_PERIOD) return 0;

        uint128 penaltyPercentage = uint128(((MIN_STAKE_PERIOD - timeHeld) * EARLY_UNSTAKE_PENALTY) / MIN_STAKE_PERIOD);
        return uint128((uint256(_amount) * uint256(penaltyPercentage)) / 100);
    }

    // Market resolution functions
    function resolveMarket(
        uint64 _marketId,
        bool _outcome
    ) external onlyOwner validMarket(_marketId) nonReentrant {
        MarketCore storage mCore = marketCores[_marketId];
        MarketData storage mData = marketData[_marketId];

        require(!mCore.isResolved, "Already resolved");
        require(
            mCore.marketType == MarketType.FINITE || 
            block.timestamp >= mCore.endTime,
            "Cannot resolve yet"
        );

        mCore.isResolved = true;
        mData.outcome = _outcome;

        _updateMarketReputations(_marketId);
        emit MarketResolved(_marketId, _outcome);
    }

    function _updateMarketReputations(uint64 _marketId) internal {
        MarketData storage mData = marketData[_marketId];
        uint256 totalParticipants = uint256(mData.yesShares) + uint256(mData.noShares);

        if (totalParticipants == 0) return;

        uint128 rewardPerShare = uint128((uint256(100) * 1e18) / totalParticipants);
        bool outcome = mData.outcome;

        // Update reputations for correct predictions
        // Note: This could be optimized further with a mapping of participants
        _updateReputationForOutcome(_marketId, outcome, rewardPerShare);
    }

    function _updateReputationForOutcome(
        uint64 _marketId,
        bool _outcome,
        uint128 _rewardPerShare
    ) internal {
        MarketData storage mData = marketData[_marketId];
        uint128 correctShares = _outcome ? mData.yesShares : mData.noShares;

        if (correctShares > 0) {
            UserReputation storage reputation = userReputations[msg.sender];
            uint128 reward = uint128((uint256(correctShares) * uint256(_rewardPerShare)) / 1e18);

            reputation.score += reward;
            reputation.successfulPredictions++;
            reputation.lastUpdateTime = uint64(block.timestamp);

            emit ReputationUpdated(msg.sender, reputation.score);
        }
    }

    // Claim rewards function
    function claimRewards(
        uint64 _marketId
    ) external validMarket(_marketId) nonReentrant {
        MarketCore storage mCore = marketCores[_marketId];
        MarketData storage mData = marketData[_marketId];
        UserPosition storage position = positions[_marketId][msg.sender];

        require(mCore.isResolved, "Market not resolved");
        require(!position.hasClaimedRewards, "Already claimed");
        require(mData.totalStake > 0, "No stake in the market");

        uint128 reward = _calculateReward(_marketId, position);
        require(reward > 0, "No reward available");

        position.hasClaimedRewards = true;
        require(stakingToken.transfer(msg.sender, reward), "Transfer failed");

        emit RewardsClaimed(_marketId, msg.sender, reward);
    }

    function _calculateReward(
        uint64 _marketId,
        UserPosition storage _position
    ) internal view returns (uint128) {
        MarketData storage mData = marketData[_marketId];
        bool outcome = mData.outcome;

        uint128 winningShares = outcome ? _position.yesShares : _position.noShares;
        if (winningShares == 0) return 0;

        uint128 totalWinningShares = outcome ? mData.yesShares : mData.noShares;
        return uint128((uint256(mData.totalStake) * uint256(winningShares)) / uint256(totalWinningShares));
    }

    // Utility functions
    function _updateCreatorReputation(address _creator) internal {
        UserReputation storage reputation = userReputations[_creator];
        reputation.marketsCreated++;
        reputation.score += 10;
        reputation.lastUpdateTime = uint64(block.timestamp);

        emit ReputationUpdated(_creator, reputation.score);
    }

    // Emergency functions
    function emergencyWithdraw(
        address _token,
        uint256 _amount
    ) external onlyOwner {
        require(_token != address(stakingToken), "Cannot withdraw staking token");
        IERC20(_token).transfer(owner(), _amount);
    }

    receive() external payable {}

    // Add this function near the other admin functions
    function addMarketCreator(address _creator) external onlyOwner {
        require(_creator != address(0), "Invalid creator address");
        require(!isMarketCreator[_creator], "Already a market creator");
        isMarketCreator[_creator] = true;
        emit MarketCreatorAdded(_creator);
    }
}
